import React, { useState } from "react";
import { Navbar, Nav, Offcanvas, Button } from "react-bootstrap";
import { Link } from "react-router-dom";

export default function Header() {
  const [showOffcanvas, setShowOffcanvas] = useState(false);

  const handleOffcanvasToggle = () => {
    setShowOffcanvas(!showOffcanvas);
  };

  // const handleLinkClick = () => {
  //   setShowOffcanvas(false);
  // };
   const [expanded, setExpanded] = useState(false);

  const handleNavbarToggle = () => {
    setExpanded(!expanded);
  };

  const handleLinkClick = () => {
    setExpanded(false);
  };
  return (
    <div>
      <div className="container">
        <div className="row">
          <Navbar bg="dark" variant="dark">
            <Navbar.Brand as={Link} to="/" onClick={handleLinkClick}>
              Your Logo
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" onClick={handleOffcanvasToggle} />
            {/* <Button variant="dark" onClick={handleOffcanvasToggle}>
              Menu
            </Button> */}
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="ms-auto">
                <Nav.Link as={Link} to="/" onClick={handleLinkClick}>
                  Home
                </Nav.Link>
                <Nav.Link as={Link} to="/about" onClick={handleLinkClick}>
                  About
                </Nav.Link>
                <Nav.Link as={Link} to="/contact" onClick={handleLinkClick}>
                  Contact
                </Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
          <Offcanvas
            show={showOffcanvas}
            onHide={() => setShowOffcanvas(false)}
          >
            <Offcanvas.Header closeButton>
              <Offcanvas.Title>Menu</Offcanvas.Title>
            </Offcanvas.Header>
            <Offcanvas.Body>
              <Nav className="flex-column">
                <Nav.Link as={Link} to="/" onClick={handleLinkClick}>
                  Home
                </Nav.Link>
                <Nav.Link as={Link} to="/about" onClick={handleLinkClick}>
                  About
                </Nav.Link>
                <Nav.Link as={Link} to="/services" onClick={handleLinkClick}>
                  Services
                </Nav.Link>
                {/* Add more Nav.Link components for additional navigation items */}
              </Nav>
            </Offcanvas.Body>
          </Offcanvas>
        </div>
      </div>
    </div>
  );
}
